		Scanner sc = new Scanner(System.in);
		int n = sc.nextInt();
		int[] s = new int[n];
		int[] l = new int[n];
		int[] p = new int[n];
		for (int i = 0; i < n; i++) {
			s[i] = sc.nextInt();
			l[i] = sc.nextInt();
			p[i] = sc.nextInt();
		}

		int[] dp = new int[400];
		fill(dp, -1);
		dp[0] = 0;

		for (int i = 0; i < n; i++) {
			for (int x = 0; x < 400; x++) {
				for (int j = s[i]; j <= l[i]; j++) {
					if (x - j >= 0) {
						dp[x] = max(dp[x], dp[x - j] + p[i]);
