import std.stdio, std.algorithm, std.conv, std.array, std.string, std.math, std.typecons, std.numeric;

void main()
{
    auto N = readln.chomp.to!int;
    int[] ss, ls, ps;
    foreach (_; 0..N) {
        auto slp = readln.split.to!(int[]);
        ss ~= slp[0];
        ls ~= slp[1];
        ps ~= slp[2];
    }
    auto DP = new int[][](N+1, 393+1);
    foreach (ref dp; DP) dp[] = int.min;
    DP[0][0] = 0;
    foreach (i; 0..N) {
        foreach (f; 0..393+1) {
            DP[i+1][f] = max(DP[i+1][f], DP[i][f]);
            foreach (d; ss[i]..ls[i]+1) {
                if (f+d <= 393) DP[i][f+d] = max(DP[i][f+d], DP[i][f] + ps[i]);
            }
        }
    }
    foreach (i; 1..N+1) {
        foreach (f; 0..393+1) {
            DP[i][f] = max(DP[i][f], DP[i-1][f]);
        }
    }
    auto M = readln.chomp.to!int;
    int[] res;
    foreach (_; 0..M) {
        auto m = DP[N][readln.chomp.to!int];
        if (m <= 0) {
            writeln(-1);
            return;
        }
        res ~= m;
    }
    foreach (r; res) writeln(r);
}

import std.stdio;
import std.c.stdio;
import std.range;
import std.array;
import std.functional;
import std.algorithm;
import std.conv;
import std.container;
import std.math;
import std.numeric;
import std.string;
import std.c.string;
import std.regex;
import std.typecons;

void main() {
    const int L = 394,
              W = 394;
    int n; readf("%d\n", &n);
    int[] P = new int[L];
    foreach (i; 0 .. n) {
        int s, l, p; readf("%d %d %d\n", &s, &l, &p);
        for (int j = s; j <= l; j++) {
            P[j] = max(P[j], p);
        }
    }

    int cache[L][W]; memset(cast(void*)cache, -1, cache.sizeof);
    int solve(int i, int w) {
        if (w == 0) return 0;
        if (i == L) return -1;
        if (cache[i][w] >= 0) return cache[i][w];
        if (P[i] == 0) return solve(i + 1, w);
        int ret = -1;
        for (int x = 0; x * i <= w; x++) {
            int r = solve(i + 1, w - x * i);
            if (r == -1) continue;
            ret = max(ret, r + x * P[i]);
        }
        return cache[i][w] = ret;
    }

    int m; readf("%d\n", &m);
    int[] ans;
    foreach (i; 0 .. m) {
        int w; readf("%d\n", &w);
        ans ~= solve(0, w);
        if (ans.back == -1) {
            writeln(-1);
            return;
        }
    }
    foreach (a; ans) {
        a.writeln;
    }
}

