def maxpoint(remin, cache=[lis, 0]):
    dp = cache[0]
    written = cache[1]
    for j in range(n):
        start = max(written - lyric[j][0] + 1, 0)
        stop = remin - lyric[j][0] + 1
        seq_index = ((lyric[j][0]//(lyric[j][1]-lyric[j][0]+1))+1)*lyric[j][0]
