                if j == 0:
                    dp[j][i+lyric[j][0]] = dp[j][i] + lyric[j][2]
                else:
                    dp[j][i+lyric[j][0]] = max(dp[j][i] + lyric[j][2], dp[j-1][i])
            elif dp[j][i] >= 0:
                for k in range(lyric[j][0], min(2*lyric[j][0], lyric[j][1], remin-i)+1):
                    if j == 0:
