use std::fmt;
use std::io::stdin;
use std::io::BufRead;
use std::str;
use std::str::FromStr;
use std::vec::Vec;
use std::cmp;

fn main() {
    let stdin = stdin();
    let mut scan = InputScanner::new(stdin.lock(), 256);

    let n:usize = scan.next();
    let slp:Vec<(usize,usize,usize)> = (0..n).map(|_| (scan.next(),scan.next(),scan.next())).collect();

    let m:usize = scan.next();
    let w:Vec<usize> = (0..m).map(|_| scan.next()).collect();

    let mut answer = Vec::with_capacity(m);

    for i in 0..m {
        let mut dp:Vec<usize> = (0..(w[i]+1)).map(|_| 0).collect();

        for slp in &slp {
            for w in 0..(w[i]+1) {
				let k = slp.0;

				if w >= k {
					dp[w] = cmp::max(dp[w],dp[w-k] + slp.2);
				} else if w > k  && dp[w-k] == 0 {
					break;
				}
            }
        }

        if dp[w[i]] == 0 {
            println!("-1");
            return;
        }

        answer.push(dp[w[i]]);
    }

    for ans in answer {
        println!("{}",ans);
    }
}
struct InputScanner<R: BufRead> {
	reader: R,
	buf: Vec<u8>, // Should never be empty
	pos: usize,   // Should never be out of bounds as long as the input ends with '\n'
}
impl<R: BufRead> InputScanner<R> {
	fn new(reader: R, capacity: usize) -> Self {
		InputScanner {
			reader: reader,
			buf: Vec::with_capacity(capacity),
			pos: 0,
		}
	}

	#[inline]
	fn next<T: FromStr>(&mut self) -> T
	where
		T::Err: fmt::Debug,
	{
		if self.buf.is_empty() {
			self._read_next_line();
		}
		let mut start = None;
		loop {
			match (self.buf[self.pos], start.is_some()) {
				(b' ', true) | (b'\n', true) | (b'\r', true) => break,
				(_, true) | (b' ', false) => self.pos += 1,
				(b'\n', false) | (b'\r', false) => self._read_next_line(),
				(_, false) => start = Some(self.pos),
			}
		}
		let target = &self.buf[start.unwrap()..self.pos];
		unsafe { str::from_utf8_unchecked(target) }.parse().unwrap()
	}

	#[inline]
	fn _read_next_line(&mut self) {
		self.pos = 0;
		self.buf.clear();
		if self.reader.read_until(b'\n', &mut self.buf).unwrap() == 0 {
			panic!("Reached EOF");
		}
	}
}

