int calc(int n){
	int ret=n/(2*b)*2;
	n%=(2*b);
	if(n>=h)ret++;
	if(n>=2*b-h)ret++;
	return ret;
}
int main(){
	scanf("%d%d%d%d%d%d%d%d",&a,&b,&c,&d,&e,&f,&g,&h);
	c*=d;
	long long ret=0LL;
	int now=g-e;
	int at=0;
	while(now<=c){
		int x=(int)(sqrt((double)c*c-(double)now*now+EPS)+EPS);
		int r=f+x;
		int l=f-x;
		r+=500000000/b*b*2;
		l+=500000000/b*b*2;
		ret+=calc(r)-calc(l-1);
		if(at%2==0){
			now+=(a-g)*2;
		}else{
			now+=g*2;
		}
		at++;
	}
	now=-e-g;
	at=0;
	while(now>=-c){
		int x=(int)(sqrt((double)c*c-(double)now*now+EPS)+EPS);
		int r=f+x;
		int l=f-x;	
		r+=500000000/b*b*2;
		l+=500000000/b*b*2;
		ret+=calc(r)-calc(l-1);
		if(at%2==0){
			now-=(a-g)*2;
		}else{
			now-=g*2;
		}
		at++;
	}
	printf("%lld\n",ret);
}

