const double EPS = 1e-10;
const double PI  = acos(-1.0);
const LL MOD = 1e9+7;

struct P{
  double x, y;
  P(double x_=0., double y_=0.):x(x_), y(y_)
  {}
  double norm() const{
	return sqrt(x*x+y*y);
  }

  P operator+(const P& p2) const{
	return P(x+p2.x, y+p2.y);
  }
  P operator-(const P& p2) const{
	return P(x-p2.x, y-p2.y);
  }

  P operator*(double sc) const{
	return P(x*sc, y*sc);
  }
};

int main(){
  cin.tie(0);
  ios_base::sync_with_stdio(false);

  P st, en;
  LL W, H, V, T, X, Y, P, Q;
  cin >> W >> H >> V >> T >> X >> Y >> P >> Q;
  if(X > P){
	X = W - X;
	P = W - P;
  }
  if(Y > Q){
	Y = H - Y;
	Q = H - Q;
  }

  T *= V;
  LL ans = 0;
  for(LL ix=-T;ix<=T;++ix){
	LL x = ix * W + (ix%2==0?P: W-P);
	LL dx = x - X;
	LL t = T * T - dx*dx;
	if(t < 0) continue;
	LL iy = (sqrt(t) + Y) / H;
	for(;;++iy){
	  LL y = iy * H + (iy%2==0?Q: H-Q);
	  LL dy = y - Y;
	  if(dx*dx + dy*dy > T*T)
		break;
	}
	ans += iy;

	iy = min(-1ll, (LL)((-sqrt(t) + Y) / H+1));
	for(;;--iy){
	  LL y = iy * H + (iy%2==0?Q: H-Q);
	  LL dy = y - Y;
	  if(dx*dx + dy*dy > T*T)
		break;
	}
	ans += max(0ll, -iy-1);
  }
  cout << ans << endl;

  return 0;
}

