long long sqr(long long ll){
  return ll*ll;
}

bool hit(int by,int bx){
  return sqr(h*by+((by&1)?h-y:y)-q)+sqr(w*bx+((bx&1)?w-x:x)-p)<=sqr(v*t);
}

int main(){
  cin>>w>>h>>v>>t>>x>>y>>p>>q;
  int i;
  for(i=-v*t/h-10;;i++){
    if(hit(i,-1)||hit(i,0)||hit(i,1))break;
  }
  int l=-2,r=2;
  long long ans=0;
  for(;;i++){
    while(hit(i,l)){
      l--;
    }
    while(!hit(i,l+1)){
      l++;
      if(r-l==1){
	cout<<ans<<endl;
	return 0;
      }
    }
    while(hit(i,r)){
      r++;
    }
    while(!hit(i,r-1)){
      r--;
    }
    ans+=r-l-1;
  }
}

