#define int long long
int w,h,v,t,l;
struct Point{
  int x,y;
  Point(){}
  Point(int x,int y):x(x),y(y){}
  Point operator+(Point a){return Point(x+a.x,y+a.y);};
  Point operator-(Point a){return Point(x-a.x,y-a.y);};
  bool operator==(Point a)const{
    return x==a.x&&y==a.y;
  }
};
Point s,e;
int norm(Point p){
  return p.x*p.x+p.y*p.y;
}
int norm2(Point p){
  p.x*=w;p.y*=h;
  p=p-s;
  //cout<<p.x*p.x+p.y*p.y<<endl;
  return p.x*p.x+p.y*p.y;
}
int check(Point p){
  int res=0;
  if(norm2(p)<=l*l) res++;
  p.x++;
  if(norm2(p)<=l*l) res++;
  p.y++;
  if(norm2(p)<=l*l) res++;
  p.x--;
  if(norm2(p)<=l*l) res++;
  p.y--;
  if(!res){
    if(p.y==0){
      if(p.x>=0){
	//cout<<s.x+p.x*w<<":"<<l<<endl;
	if(s.x+p.x*w<=l) return 5;
      }else{
	if(-l<=s.x+(p.x+1)*w) return 5;
      }
    }
    if(p.x==0){
      if(p.y>=0){
	if(s.y+p.y*h<=l) return 5;
      }else{
	if(-l<=s.y+(p.y+1)*h) return 5;
      }
    }
  }
  return res;
}
int check2(Point p){
  if(check(p)==4) return 0;
  Point r=Point(p.x*w,p.y*h);
  if(abs(p.x)%2) r.x+=w-e.x;
  else r.x+=e.x;
  if(abs(p.y)%2) r.y+=h-e.y;
  else r.y+=e.y;
  return norm(r-s)<=l*l;
}

