typedef long long ll;

ll w,h,v,t,x,y,p,q;

int main(){
  cin >> w >> h >> v >> t >> x >> y >> p >> q;

  ll num = (v*t+y)/h;
  ll pos = num*h + ((num&1LL)?(h-q):q);

  ll res = 0;
  while(true){
    if(pos < -v*t+y)break;
    if(pos <= v*t+y){
      ll len = v*t*v*t - (pos-y)*(pos-y);
      ll l = 0, r = (v*t+w-1)/w+10LL;
      while(r-l>1){
	ll mid = (r+l)/2;
	ll pos = (mid-1)*w - x;
	if(mid&1LL)pos += p;
	else pos += w-p;
	if(pos*pos <= len)l = mid;
	else r = mid;
      }
      res += l;

      l = 0, r = (v*t+w-1)/w+10LL;
      while(r-l>1){
	ll mid = (r+l)/2;
	ll pos = (mid-1)*w + x;
	if(mid&1LL)pos += p;
	else pos += w-p;
	if(pos*pos <= len)l = mid;
	else r = mid;
      }
      res += l;
    }

    if(num&1LL)pos -= 2*(h-q);
    else pos -= 2*q;
    num--;
  }

  cout << res << endl;
}

