constexpr double eps = 1e-12;

int64_t mod(int64_t x, int64_t mod) {
  if (x >= 0) return x%mod;
  else return mod + x%mod;
}

int64_t solve(int64_t range, int64_t x, int64_t y, int64_t modx, int64_t mody) {
  int64_t sum = 0;
  for (int64_t i = -range; i <= range; ++i) {
    if (mod(i, mody) != mod(y, mody)) continue;
     int64_t d = sqrt(range*range - i*i + eps);
     sum += floor((d-x)/double(modx)+eps) - ceil((-d-x)/double(modx)-eps) + 1;
  }
  return sum;
}

int main() {
  int64_t w,h,v,t,x,y,p,q;
  cin>>w>>h>>v>>t>>x>>y>>p>>q;
  int64_t vt = v*t;
  int64_t modx = 2*w;
  int64_t mody = 2*h;
  int64_t res = solve(vt, p-x, q-y, modx, mody)
      + solve(vt, modx-p-x, q-y, modx, mody)
      + solve(vt, p-x, mody-q-y, modx, mody)
      + solve(vt, modx-p-x, mody-q-y, modx, mody);
  cout << res << endl;
  return 0;
}

