        ll dx = X - x, dy = Y - y;
        if(dx*dx + dy*dy <= T*T*V*V) {
            ub = mid;
        }
        else {
            lb = mid;
        }
    }
    return ub;
}

ll solve(ll cx, ll tx, ll cy, ll ty) {
    ll ret = 0;
    for(int k=-T*V; k<=T*V; k++) {
        ll X = cx*k + tx, dx = X - x;
        if(dx*dx > T*T*V*V) continue;
        ll ub = find_upper(X, cy, ty);
        ll lb = find_lower(X, cy, ty);
        ret += ub - lb;
        // printf("X = %lld, ub = %lld, lb = %lld\n", X, ub, lb);
    }
    // printf("ret = %lld\n", ret);
    return ret;
}

int main() {
    cin >> W >> H >> V >> T >> x >> y >> p >> q;

    ll ans = 0;
    ans += solve(2*W,     p, 2*H,     q);
    ans += solve(2*W,     p, 2*H, 2*H-q);
    ans += solve(2*W, 2*W-p, 2*H,     q);
    ans += solve(2*W, 2*W-p, 2*H, 2*H-q);

    cout << ans << endl;
    return 0;
}

