using namespace std;

constexpr double eps = 1e-8;

int64_t mod(int64_t x, int64_t mod) {
  if (x >= 0) return x%mod;
  else return mod + x%mod;
}

int64_t solve(int64_t range, int64_t x, int64_t y, int64_t modx, int64_t mody) {
  int64_t sum = 0;
  for (int64_t i = -range; i <= range; ++i) {
    if (mod(i, mody) != y) continue;
     double d = sqrt(range*range - i*i + eps);
     sum += floor((d-x)/double(modx)) - ceil((-d-x)/double(modx)) + 1;
  }
  return sum;
}

