long long w, h, v, t, x, y, p, q, r;

long long solve();
long long calc(long long a, long long b);

int main() {
  cin >> w >> h >> v >> t >> x >> y >> p >> q;
  cout << solve() << endl;
  return 0;
}

long long solve() {
  long long res = 0;
  r = v * t;
  res += calc(p - x, q - y);
  res += calc(-p - x, q - y);
  res += calc(p - x, -q - y);
  res += calc(-p - x, -q - y);
  return res;
}

long long calc(long long a, long long b) {
  long long res = 0;
  for(long long i = -4000000; i <= 4000000; ++i) {
    long long nx = 2 * i * w + a, d, u;
    if(abs(nx) > r) continue;
    d = u = sqrt(r * r - nx * nx);
    u = (u - b) / (2 * h);
    d = ceil(-(d + b) / (long double)(2 * h));
    if(u == d &&
       nx * nx + (2 * h * u + b) * (2 * h * u + b) > r * r)
      continue;
    res += max(0LL, u - d + 1);
  }
  return res;
}

