long long calc(int w, int h, int v, int t, int x, int y, int p, int q) {
  long long c = 0;
  long long d = v * t;
  for (int i = -d / w - 1; i < d / w + 1; ++i) {
    long long tx = i * w + (i % 2 ? w - p : p);
    if (abs(tx - x) > d) {
      continue;
    }
    double dy = sqrt(d * d - (tx - x) * (tx - x));
    int r[] = { (dy + y) / h, -(dy + h - y) / h };
    if (r[0] == r[1]) {
      if ((tx - x) * (tx - x) + (q - y) * (q - y) < d * d) c++;
    } else {
      c += r[0] - r[1] - 1;
      for (int k = 0; k < 2; ++k) {
        long long ty = r[k] * h + (r[k] % 2 ? h - q : q);
        if ((tx - x) * (tx - x) + (ty - y) * (ty - y) < d * d) c++;
      }
    }
  }
  return c;
}

