  for(int i=bt.x;s.x-l<=(i+1)*w;i--){
    tp.x=bt.x=i;

    nt=tp;nb=bt;
    nt.y++;nb.y--;
    while(check(nt)==4){
      tp=nt;
      nt.y++;
    }
    while(check(nb)==4){
      bt=nb;
      nb.y--;
    }

    nt=tp;nb=bt;
    nt.y--;nb.y++;
    while(check(tp)!=4&&tp.y>bt.y){
      tp.y--;
    }
    while(check(bt)!=4&&tp.y>bt.y){
      bt.y++;
    }

    nt=tp;nb=bt;
    while(check(nt)){
      ans+=check2(nt);
      nt.y++;
    }
    if(tp==bt) nb.y--;
    while(check(nb)){
      ans+=check2(nb);
      nb.y--;
    }
    //cout<<tp.x<<" "<<tp.y<<":"<<bt.x<<" "<<bt.y<<endl;
    //cout<<check(tp)<<" "<<check(bt)<<endl;
    if(check(tp)==4&&check(bt)==4) ans+=max(tp.y-bt.y+1,0LL);
  }

  cout<<ans<<endl;
  return 0;
}

