    ans += solve(2*W,     p, 2*H,     q);
    ans += solve(2*W,     p, 2*H, 2*H-q);
    ans += solve(2*W, 2*W-p, 2*H,     q);
    ans += solve(2*W, 2*W-p, 2*H, 2*H-q);

    cout << ans << endl;
    return 0;
}

