#define REP(i,s,n) for(int i=s;i<n;i++)
#define rep(i,n) REP(i,0,n)

using namespace std;

typedef long long ll;

ll w,h,v,t,x,y,p,q,R,answer;

inline ll getDist(ll dx,ll dy) { return dx*dx+dy*dy; }

ll countValue(bool phase,ll coef_x){
  ll enemy_x = p;
  if( coef_x >= 0 ) {
    if( ( coef_x & 1LL ) ) enemy_x = w - enemy_x;
    enemy_x += (coef_x*w);
  }
  else {
    if( coef_x & 1LL ) enemy_x = ( coef_x * w) + w - p;
    else               enemy_x = ( coef_x * w) + p;
  }

  ll coef_y_L = 0, coef_y_R = R / h + ( R % h != 0LL ) + 1;
  ll binary_search_solution = 0;
  ll prev = LLONG_MAX;
  rep(_,33){
    ll coef_y = ( coef_y_L + coef_y_R ) / 2LL;
    if( coef_y == prev ) break;
    prev = coef_y;
    if( !phase ) coef_y *= -1LL;
    ll enemy_y = q;
    if( phase ) {
      if( ( coef_y & 1LL ) ) enemy_y = h - enemy_y;
      enemy_y += (coef_y*h);
    } else {
      if( coef_y & 1LL ) enemy_y = ( coef_y * h) - ( h - q );
      else               enemy_y = ( coef_y * h) - q;
    }

    if(  phase ) assert( enemy_y >= 0LL );
    if( !phase ) assert( enemy_y <= 0LL );
    ll dist = getDist(x-enemy_x,y-enemy_y);

    if( dist <= R*R ) {
      binary_search_solution = max(binary_search_solution,llabs(coef_y) + 1LL);
      coef_y_L = llabs(coef_y);
    } else {
      coef_y_R = llabs(coef_y);
    }
  }

  return binary_search_solution;
}

int main(){
  cin >> w >> h >> v >> t >> x >> y >> p >> q;
  R = v * t;
  ll coef = R / w + ( R % w != 0LL );
  for(ll coef_x=-coef;coef_x<=coef;coef_x++){
    answer += countValue(true,coef_x);
    answer += countValue(false,coef_x);
  }
  cout << answer << endl;
  return 0;
}

