using namespace std;
using ll = long long int;

const ll INF = 1LL << 60;
ll W, H, V, T, x, y, p, q;

ll find_upper(ll X, ll cy, ll ty, ll piv) {
    ll ub = 2*T*V + ty, lb = piv-1;
    while(ub - lb > 1) {
        ll mid = (ub + lb) / 2;
        ll Y = cy*mid + ty;

        ll dx = X - x, dy = Y - y;
        // printf("mid = %lld, dx = %lld, dy = %lld, lim = %lld\n", mid, dx, dy, T*T*V*V);
        if(dx*dx + dy*dy > T*T*V*V) {
            ub = mid;
        }
        else {
            lb = mid;
        }
    }
    return ub;
}

ll find_lower(ll X, ll cy, ll ty, ll piv) {
    ll ub = piv, lb = -2*T*V - ty;
    while(ub - lb > 1) {
        ll mid = (ub + lb) / 2;
        ll Y = cy*mid + ty;

        ll dx = X - x, dy = Y - y;
        if(dx*dx + dy*dy <= T*T*V*V) {
            ub = mid;
        }
        else {
            lb = mid;
        }
    }
    return ub;
}

bool is_valid(ll X, ll cy, ll ty, ll k) {
    ll Y = cy*k + ty;
    ll dx = X - x, dy = Y - y;
    return dx*dx + dy*dy <= T*T*V*V;
}

ll solve(ll cx, ll tx, ll cy, ll ty) {
    ll ret = 0;
    for(ll k=-T*V-tx; k<=T*V+tx; k++) {
        ll X = cx*k + tx, dx = X - x;
        if(dx*dx > T*T*V*V) continue;

        ll piv = INF;
        for(int k=-1; k<=1; k++) {
            if(is_valid(X, cy, ty, k)) {
                piv = k;
                break;
            }
        }
        if(piv == INF) continue;
        ll ub = find_upper(X, cy, ty, piv);
        ll lb = find_lower(X, cy, ty, piv);
        ret += ub - lb;
        // printf("X = %lld, ub = %lld, lb = %lld\n", X, ub, lb);
    }
    // printf("ret = %lld\n", ret);
    return ret;
}

