  for(ll n = -2000000; n <= 2000000; n++) {
    ll x = 2 * n * W + p - X;

    if(-r <= x && x <= r && x * x + (q - Y) * (q - Y) <= r * r) {
      ll ok1 = 0, ng1 = 2000000;
      while(abs(ng1 - ok1) > 1) {
        ll m = (ok1 + ng1) / 2;
        ll y = 2 * m * H + q - Y;
        (x * x + y * y <= r * r ? ok1 : ng1) = m;
      }

      ll ok2 = 0, ng2 = -2000000;
      while(abs(ng2 - ok2) > 1) {
        ll m = (ok2 + ng2) / 2;
        ll y = 2 * m * H + q - Y;
        (x * x + y * y <= r * r ? ok2 : ng2) = m;
      }

      ret += ok1 - ok2 + 1;

      // ll y1 = 2 * ok1 * H + q - Y, y2 = 2 * ok2 * H + q - Y;
      // cerr << "(" << x << ", " << y1 << "), " << "(" << x << ", " << y2 << ")" << endl;
    }
  }

  return ret;
}

int main(void) {
  cin >> W >> H >> V >> T >> X >> Y >> P >> Q;

  ll ans = 0;
  ans += solve(+P, +Q);
  ans += solve(+P, -Q);
  ans += solve(-P, +Q);
  ans += solve(-P, -Q);
  cout << ans << endl;
}

