{
	long long distances = 1LL * (x1 - x) * (x1 - x) + 1LL * (y1 - y) * (y1 - y);

	return distances <= r;
}

long long solve()
{
	if (!check(p, q))
	{
		return 0;
	}

	long long ret = 0;


	// ------ 1st. ------ //

	int l1 = -5000000 / w;
	int r1 = 0;

	while (r1 - l1 > 1)
	{
		int m1 = (l1 + r1) / 2;

		if (check(2LL * w * m1 + p, q))
		{
			r1 = m1;
		}
		else
		{
			l1 = m1;
		}
	}

	int l2 = 0;
