			if (check(xs, 2LL * h * m6 + q))
			{
				l6 = m6;
			}
			else
			{
				r6 = m6;
			}
		}

		if (r6 - r5 == 1)
		{
			ret += check(xs, 2LL * h * r5 + q) ? 1 : 0;
		}
		else
		{
			ret += r6 - r5;
		}


		// ------ 2nd. ------ //

		int l7 = -5000000 / h;
		int r7 = 0;

		while (r7 - l7 > 1)
