#define BIG_NUM 2000000000
#define MOD 1000000007
#define EPS 0.000000001
using namespace std;



struct Point{
	ll x,y;
};

ll W,H,V,T,X,Y,P,Q,R;
ll num_row,num_col,center_row,center_col;
Point center;

Point calc_loc(ll row,ll col){

	Point point;
	point.x = col*W;

	if(abs(center_col-col)%2 == 0){
		point.x += P;
	}else{
		point.x += (W-P);
	}

	point.y = (row*H);

	if(abs(center_row-row)%2 == 0){
		point.y += Q;
	}else{
		point.y += (H-Q);
	}
	return point;
}


ll calc_dist(Point a,Point b){
	return ((a.x-b.x)*(a.x-b.x)+(a.y-b.y)*(a.y-b.y));
}

ll calc_left(ll row){

	ll left = 0,right = center_col,m = (left+right)/2;
	ll res = BIG_NUM;

	Point point;

	while(left <= right){
		point = calc_loc(row,m);

		if(calc_dist(point,center) <= R*R){
			res = m;
			right = m-1;
		}else{
			left = m+1;
		}
		m = (left+right)/2;
	}

	return res;
}

ll calc_right(ll row){

	ll left = center_col,right = num_col-1,m = (left+right)/2;
	ll res = num_row;

	Point point;

	while(left <= right){
		point = calc_loc(row,m);

		if(calc_dist(point,center) > R*R){
			res = m;
			right = m-1; //より左へ
		}else{
			left = m+1; //右へ
		}
		m = (left+right)/2;
	}

	return res;
}

int main(){

	scanf("%lld %lld %lld %lld %lld %lld %lld %lld",&W,&H,&V,&T,&X,&Y,&P,&Q);

	R = V*T;

	num_row = 2*(R/H+2)+1;
	num_col = 2*(R/W+2)+1;

	center.x = W*(R/W+2)+X;
	center.y = H*(R/H+2)+Y;

	center_row = (R/H+2);
	center_col = (R/W+2);

	ll ans = 0,left,right;

	for(ll row = num_row-1; row >= 0; row--){
		left = calc_left(row);
		if(left == BIG_NUM)continue;
		right = calc_right(row);
		ans += (right-left);
	}

	printf("%lld\n",ans);

	return 0;
}

