int UP(D h){
  D ny=abs(y-Y);
  int idx=1;
  if(y<=Y)ny=dy[1]-(Y-y),idx=0;
  //cout<<ny<<' '<<h<<endl;
  //ny-=abs(y-Y);
  int l=0,r=1e6;
  while(l<r){
    int mid=(l+r)/2;
    bool flag=0;
    if(!mid)flag=1;
    else if(mid==1)flag=(ny<=h);
    else{
      D NY=ny;
      NY+=dy[0]*((mid-1)/2);
      NY+=dy[1]*((mid-1)/2);
      if((mid-1)%2) NY+=dy[idx];
      if(NY<=h)flag=1;
      else flag=0;
      //if(mid==2)cout<<NY<<endl;
    }
    if(!flag)r=mid;
    else l=mid+1;
  }
  return l-1;
}

int DOWN(D h){
  D ny=-abs(Y-y);//y-Y;
  int idx=0;
  if(y>=Y)ny=-dy[0]+(y-Y),idx=1;
  //cout<<ny<<endl;
