  int s1=UP(h);
  int s2=DOWN(h);
  if(y==Y)s1++;
  //cout<<h<<' '<<s1<<' '<<s2<<endl;
  return (s1+s2)%mod;
}

int LEFT(){
  D nx=x;
  int idx=0,ans=0;
  if(X<=x)nx=x-dx[0],idx=1;
  while(1){
    //cout<<nx<<endl;
    D h=get_h(abs(nx-X));
    if(h<0)break;
    h=sqrt(h);
    //cout<<nx-X<<endl;
    ans+=Binary_search(h);
    ans%=mod;
    nx-=dx[idx++];
    idx%=2;
  }
  return ans;
}

int RIGHT(){
