  r=v*t;w*=2.0;h*=2.0;
  for(double i=p;i-x-r<eps;i+=w)res+=count(i);
  for(double i=-p;i-x-r<eps;i+=w)res+=count(i);
  for(double i=p-w;x-r-i<eps;i-=w)res+=count(i);
  for(double i=-p-w;x-r-i<eps;i-=w)res+=count(i);
  return res;
}

