    else{
      D NY=ny;
      NY+=dy[0]*((mid-1)/2);
      NY+=dy[1]*((mid-1)/2);
      if((mid-1)%2) NY+=dy[idx];
      if(NY<=h)flag=1;
      else flag=0;
      //if(mid==2)cout<<NY<<endl;
    }
    if(!flag)r=mid;
    else l=mid+1;
  }
  return l-1;
}

int DOWN(D h){
  D ny=-abs(Y-y);//y-Y;
  int idx=0;
  if(y>=Y)ny=-dy[0]+(y-Y),idx=1;
  //cout<<ny<<endl;
