  //cout<<ny<<' '<<h<<endl;
  int l=0,r=1e6;
  while(l<r){
    int mid=(l+r)/2;
    bool flag=0;
    if(!mid)flag=1;
    else if(mid==1)flag=(abs(ny)<=h);
    else{
      D NY=ny;
      NY-=dy[0]*((mid-1)/2);
      NY-=dy[1]*((mid-1)/2);
      if((mid-1)%2) NY-=dy[idx];
      if(abs(NY)<=h)flag=1;
      else flag=0;
    }
    if(!flag)r=mid;
    else l=mid+1;
  }
  return l-1;
}
