struct point{
  ll x,y;
};

ll w,h,v,t,x,y,p,q;
ll R,H,W;
point o,O;

point calc(ll X,ll Y){
  ll dx=abs(X-O.x),dy=abs(Y-O.y);
  point res;

  if(dx%2==0)res.x=p;
  else res.x=w-p;

  if(dy%2==0)res.y=q;
  else res.y=h-q;

  res.x+=X*w;
  res.y+=Y*h;
  return res;
}

ll search(ll Y){
  ll l=0,r=W,m;
  while(l<r){
    m=(l+r)/2;
    point d=calc(m,Y);
    ll dist= (d.x-o.x)*(d.x-o.x) + (d.y-o.y)*(d.y-o.y);
    if( dist <= v*t*v*t  || o.x< d.x ){
      r=m;
    }else{
      l=m+1;
    }
  }
  return l;
}

ll search2(ll Y){
  ll l=0,r=W,m;
  while(l<r){
    m=(l+r)/2;
    point d=calc(m,Y);
    ll dist= (d.x-o.x)*(d.x-o.x) + (d.y-o.y)*(d.y-o.y);
    if( dist <= v*t*v*t || d.x < o.x ){
      l=m+1;
    }else{
      r=m;
    }
  }
  return l;
}

