#define int long long
int H, W, V, T, X, Y, P, Q;
int R;
int cnt(int x, int y) {
	if (R < abs(x))return 0;
	double t = sqrt(R*R - x*x);
	int b1 = ((t - y) / (2 * H)) + 3;
	int b2 = ((-t - y) / (2 * H)) - 3;
	int c = 0;
	while (true) {
		if (x*x + (y + 2 * b1 * H)*(y + 2 * b1 * H) <= R*R) {
			break;
		}
		b1--;
		c++;
		if (c == 10)break;
	}
	if (c == 10)return 0;
	c = 0;
	while (true) {
		if (x*x + (y + 2 * b2 * H)*(y + 2 * b2 * H) <= R*R) {
			break;
		}
		b2++;
		c++;
		if (c == 10)break;
	}
	if (c == 10)return 0;
	return b1 - b2 + 1;
}
int solve(int x, int y) {
	x = abs(x) % (2 * W);
	y = abs(y) % (2 * H);
	int res = cnt(x, y);
	//cerr << x << " " << y << " " << res << endl;
	int t;
	int a = 1;
	while (true) {
		t = cnt(x + 2 * a*W, y);
		if (t == 0)break;
		res += t;
		a++;
	}
	a = -1;
	while (true) {
		t = cnt(x + 2 * a*W, y);
		if (t == 0)break;
		res += t;
		a--;
	}
	return res;
}
