	cin >> W >> H >> V >> T >> P >> Q >> X >> Y;
	R = V*T;
	int res = 0;
	res += solve(X - P, Y - Q);
	res += solve(2 * W - X - P, Y - Q);
	res += solve(X - P, 2 * H - Y - Q);
	res += solve(2 * W - X - P, 2 * H - Y - Q);
	cout << res << endl;
}

