  int64_t modx = 2*w;
  int64_t mody = 2*h;
  int64_t res = solve(vt, p-x, q-y, modx, mody)
      + solve(vt, modx-p-x, q-y, modx, mody)
      + solve(vt, p-x, mody-q-y, modx, mody)
      + solve(vt, modx-p-x, mody-q-y, modx, mody);
  cout << res << endl;
  return 0;
}

