using namespace std;



struct Point{
	ll x,y;
};

ll W,H,V,T,X,Y,P,Q,R;
ll num_row,num_col,center_row,center_col;
Point center;

Point calc_loc(ll row,ll col){

	Point point;
	point.x = col*W;

	if(abs(center_col-col)%2 == 0){
		point.x += P;
	}else{
		point.x += (W-P);
	}

	point.y = (row*H);

	if(abs(center_row-row)%2 == 0){
		point.y += Q;
	}else{
		point.y += (H-Q);
	}
	return point;
}


ll calc_dist(Point a,Point b){
	return ((a.x-b.x)*(a.x-b.x)+(a.y-b.y)*(a.y-b.y));
}

ll calc_left(ll row){

	ll left = 0,right = center_col,m = (left+right)/2;
	ll res = BIG_NUM;

	Point point;

	while(left <= right){
		point = calc_loc(row,m);

		if(calc_dist(point,center) <= R*R){
			res = m;
			right = m-1;
		}else{
			left = m+1;
		}
		m = (left+right)/2;
	}

	return res;
}

ll calc_right(ll row){

	ll left = center_col,right = num_col-1,m = (left+right)/2;
	ll res = num_col;

	Point point;

	while(left <= right){
		point = calc_loc(row,m);

		if(calc_dist(point,center) > R*R){
			res = m;
			right = m-1;
		}else{
			left = m+1;
		}
		m = (left+right)/2;
	}

	return res;
}

