	scanf("%lld %lld %lld %lld %lld %lld %lld %lld",&W,&H,&V,&T,&X,&Y,&P,&Q);

	R = V*T;

	num_row = 2*(R/H+2)+1;
	num_col = 2*(R/W+2)+1;

	center.x = W*(R/W+2)+X;
	center.y = H*(R/H+2)+Y;

	center_row = (R/H+2);
	center_col = (R/W+2);

	ll ans = 0,left,right;

	for(ll row = num_row-1; row >= 0; row--){
		left = calc_left(row);
		if(left == BIG_NUM)continue;
		right = calc_right(row);
		ans += (right-left);
	}

	printf("%lld\n",ans);

	return 0;
}

