D get_h(double a){
  return r*r-a*a;
}

int UP(D h){
  D ny=abs(y-Y);
  int idx=1;
  if(y<=Y)ny=dx[1]-(Y-y),idx=0;
  //cout<<ny<<' '<<h<<endl;
  //ny-=abs(y-Y);
  int l=0,r=1e6;
  while(l<r){
    int mid=(l+r)/2;
    bool flag=0;
    if(!mid)flag=1;
    else if(mid==1)flag=(ny<=h);
    else{
      D NY=ny;
      NY+=dy[0]*((mid-1)/2);
      NY+=dy[1]*((mid-1)/2);
      if((mid-1)%2) NY+=dy[idx];
      if(NY<=h)flag=1;
      else flag=0;
      //if(mid==2)cout<<NY<<endl;
    }
    if(!flag)r=mid;
    else l=mid+1;
  }
  return l-1;
}

int DOWN(D h){
  D ny=-abs(Y-y);//y-Y;
  int idx=0;
  if(y>=Y)ny=-dy[0]+(y-Y),idx=1;
  //ny+=abs(y-Y);
  //cout<<ny<<' '<<h<<endl;
  int l=0,r=1e6;
  while(l<r){
    int mid=(l+r)/2;
    bool flag=0;
    if(!mid)flag=1;
    else if(mid==1)flag=(abs(ny)<=h);
    else{
      D NY=ny;
      NY-=dy[0]*((mid-1)/2);
      NY-=dy[1]*((mid-1)/2);
      if((mid-1)%2) NY-=dy[idx];
      if(abs(NY)<=h)flag=1;
      else flag=0;
    }
    if(!flag)r=mid;
    else l=mid+1;
  }
  return l-1;
}
int Binary_search(D h){
  int s1=UP(h);
  int s2=DOWN(h);
  if(y==Y)s1++;
  //cout<<r<<' '<<s1<<' '<<s2<<endl;
  return (s1+s2)%mod;
}

int LEFT(){
  D nx=x;
  int idx=0,ans=0;
  if(X<=x)nx=x-dx[0],idx=1;
  while(1){
    //cout<<nx<<endl;
    D h=get_h(abs(nx-X));
    if(h<0)break;
    h=sqrt(h);
    //cout<<nx<<endl;
    ans+=Binary_search(h);
    ans%=mod;
    nx-=dx[idx++];
    idx%=2;
  }
  return ans;
}

int RIGHT(){
  D nx=x;
  int idx=1,ans=0;
  if(X>=x)nx=x+dx[1],idx=0;
  while(1){
    //cout<<nx-X<<endl;
    D h=get_h(abs(nx-X));
    //cout<<h<<endl;
    if(h<0)break;
    h=sqrt(h);
    //cout<<nx<<endl;
    ans+=Binary_search(h);
    ans%=mod;
    nx+=dx[idx++];
    idx%=2;
  }
  return ans;
}

