ll bsu(ll d, ll i)
{
	ll ub = 1111111, lb = -1;
	while (ub - lb > 1)
	{
		ll mid = (ub + lb) / 2;
		ll ty = 2 * mid * h + d;
		if (check(i, ty)) lb = mid;
		else ub = mid;
	}
	return lb + 1;
}

ll bsd(ll d, ll i)
{
	ll ub = 1111111, lb = -1;
	while (ub - lb > 1)
	{
		ll mid = (ub + lb) / 2;
		ll ty = - 2 * mid * h + d;
		if (check(i, ty)) lb = mid;
		else ub = mid;
	}
	return lb + 1;
}

