#define MAX_N 10000000
#define INF 10000000

long long H, W, V, T, X, Y, P, Q;

int main() {
	cin >> W >> H >> V >> T >> X >> Y >> P >> Q;
	long long K = V*T;
	long long x[4] = { P,2 * W - P,P,2 * W - P };
	long long y[4] = { Q,Q,2 * H - Q,2 * H - Q };
	long long res = 0;
	for (int i = 0; i < 4; i++) {
		for (int j = -(MAX_N); j <= MAX_N; j++) {
			long long X1 = x[i] + (2 * W*j);
			if (abs(X1) >= 2000000000) { continue; }
			long long XD = (X1 - X)*(X1 - X);
			if (XD <= K*K) {
				long double YD = sqrtl(1.0*(K*K - XD));
				long double LY = Y - YD, RY = Y + YD;
				long double LS = LY - y[i];
				long double RS = RY - y[i];
				LS /= (2 * H); RS /= (2 * H);
				long long P = (long long)(LS + INF + 0.999999999) - INF;
				long long Q = (long long)(RS + INF) - INF;
				res += Q - P + 1;
			}
		}
	}
	cout << res << endl;
	return 0;
}

