  int xmin,xmax,xpos,ypos,xlim,ylower,yupper;
  ll total2,ret,ypos2,xpos2;

  scanf("%d %d %d %d %d %d %d %d",&w,&h,&v,&t,&x,&y,&p,&q);
  total = v*t;
  total2=(ll)total*total;
  yupper=  total/h+1;
  ylower= -total/h-1;
  ret=0;

  for(i=ylower;i<=yupper;i++)
    {
      ypos=h*i+((i&1)?(h-q):q);
      ypos2=(ll)(ypos-y)*(ypos-y);
