using namespace std;

char kiban[20][15];
int X[8]={0,1,0,-1,1,-1,-1,1};
int Y[8]={-1,0,1,0,1,-1,1,-1};
int ans=inf;

bool in(int h,int w)
{
  if(h<0 || w<0 || 15<=w || 21<=h)return false;
  return true;
}

void move(int h,int w,int sum)
{
  bool check[8]={};
  if(18<=h){
    ans=min(ans,sum);
    return;
    }
  for(int k=0;k<8;k++){
    if(kiban[h+Y[k]][w+X[k]]=='.')continue;
    for(int i=1;i<40;i++){
      int a=h+Y[k]*i,b=w+X[k]*i;
      if(a==18){
	ans=min(ans,sum+1);
	continue;
      }
      if(!in(a,b))break;
      if(kiban[a][b]=='.'){
	check[k]=true;
	break;
      }
    }
  }
  for(int k=0;k<8;k++){
    if(!check[k])continue;
    int a=h,b=w;
    for(int i=1;i<20;i++){
      a+=Y[k];
      b+=X[k];
      if(kiban[a][b]=='.')break;
      kiban[a][b]='.';
    }
    move(a,b,sum+1);
    int H=h,W=w;
    for(int i=1;i<20;i++){
      H+=Y[k];
      W+=X[k];
      if(H==a && W==b)break;
      kiban[H][W]='x';
    }
  }
}

int main()
{
  int h,w;
  for(int i=0;i<19;i++){
    for(int j=0;j<15;j++){
      cin>>kiban[i][j];
      if(kiban[i][j]=='O'){
	h=i;
	w=j;
	kiban[i][j]='.';
      }
    }
  }
  for(int i=0;i<15;i++)kiban[19][i]='.';
  move(h,w,0);
  if(ans==inf)cout<<-1<<endl;
  else cout<<ans<<endl;
  return 0;
}

#include <iostream>
#define inf 1000000000

using namespace std;

const int W = 15, H = 20;
char map[W][H];
int sx, sy;
const int vx[] = {1, 1, 0, -1, -1, -1, 0, 1}, vy[] = {0, -1, -1, -1, 0, 1, 1, 1};

int dfs(int x, int y)
{
	int nx, ny;
	int ret = inf;
	if(y >= H-2) return 0;

	for(int d = 0; d < 8; d++){
		nx = x + vx[d], ny = y + vy[d];
		if(nx < 0 || nx >= W || ny < 0 || ny >= H) continue;
		if(map[nx][ny] == '.') continue;
		while(1){
			nx += vx[d], ny += vy[d];
			if(ny >= H-1){ ret = 0; break;}
			if(nx < 0 || nx >= W || ny < 0) break;
			if(map[nx][ny] == '.'){
				for(int k = 1; x + k*vx[d] != nx || y + k*vy[d] != ny; k++) map[x + k*vx[d]][y + k*vy[d]] = '.';
				ret = min(ret, dfs(nx, ny));
				for(int k = 1; x + k*vx[d] != nx || y + k*vy[d] != ny; k++) map[x + k*vx[d]][y + k*vy[d]] = 'X';
				break;
			}
		}
	}
	return ret + 1;
}

int main(void)
{
	for(int y = 0; y < H; y++){
		for(int x = 0; x < W; x++){
			cin >> map[x][y];
			if(map[x][y] == 'O'){
				sx = x, sy = y;
				map[x][y] = '.';
			}
		}
	}
	for(int x = 0; x < W; x++) map[x][H-1] = '.';

	int ans = dfs(sx, sy);
	if(ans >= inf) ans = -1;
	cout << ans << endl;

	return 0;
}

