			if(mask >> id & 1) continue;
			//cerr << "nx : " << nx << ", ny : " << ny << endl;
			int nmask = mask;
			while(1) {
				nmask |= 1 << id;
				nx += dx[k], ny += dy[k];
				if(!in(nx, ny)) break;
				id = b[ny][nx];
				if(id == -1 || (mask >> id & 1)) break;
			}
			if(ny >= 18) {
				cout << t + 1 << endl;
				return 0;
			}
			if(in(nx, ny)) {
				S ns = { nx, ny, nmask };
				if(dp.count(ns.hash()) == 0) {
					dp[ns.hash()] = t + 1;
					q.push(ns);
				}
			}
		}
	}
	cout << -1 << endl;
}

