const int W = 15, H = 19;
const int dx[] = {0, 1, 1, 1, 0, -1, -1, -1};
const int dy[] = {-1, -1, 0, 1, 1, 1, 0, -1};

bool inRange(int x, int y) {
	return 0 <= x && x < W && 0 <= y && y < H;
}
bool isGoal(int x, int y) {
	return y >= H || (y == H-1 && inRange(x, y));
}

struct Node {
	int x, y, f;
};

vector<P> v;
map<int, map<int, map<int, int> > > dist;
int sid[H][W];

Node Go(const Node& node, int dir) {
	Node res = node;
	int x = node.x, y = node.y, f = node.f;
//	cout << "Go:" << x << " " << y << " " << dir << " " << f << endl;
	int nx = x+dx[dir], ny = y+dy[dir];
	if ( isGoal(x, y) ) return res;
	if ( !inRange(x, y) ) return {0, 0, -1};
	if ( sid[y][x] < 0 || (f & (1 << sid[y][x])) ) return res;
	res.x = nx, res.y = ny;
	res.f = f | (1 << sid[y][x]);
	return Go(res, dir);
}
Node GoFirst(const Node& node, int dir) {
	Node res = {0, 0, -1};
	int x = node.x, y = node.y, f = node.f;
	int nx = x+dx[dir], ny = y+dy[dir];
	if ( !inRange(nx, ny) ) return res;
	if ( sid[ny][nx] < 0 || (f & (1 << sid[ny][nx])) ) return res;
	res.x = nx;
	res.y = ny;
	res.f = f;
	return Go(res, dir);
}

