		if (m[y][x] == 'O') {
			sx = x, sy = y;
		}
		if (m[y][x] == 'X') {
			v.pb( P(x, y) );
		}
	}
	assert(sx >= 0);

	int N = v.size();
	fill(sid[0], sid[H], -1);
	REP(i, N) {
		int x = v[i].first, y = v[i].second;
		sid[y][x] = i;
	}

	ll ans = 0;
	queue<Node> Q; Q.push({sx, sy, 0});
//	fill(dist[0][0], dist[H-1][W], inf);
	dist[sy][sx][0] = 1;
	if (isGoal(sx, sy)) ans = 1;
	while ( !Q.empty() ) {
		Node node = Q.front(); Q.pop();
		int x = node.x, y = node.y, f = node.f;
//		cout << x << " " << y << " " << f << endl;
		REP(i, 8) {
			Node n = GoFirst(node, i);
			if (n.f >= 0) {
				if ( isGoal(n.x, n.y) ) {
					if (ans == 0 || dist[y][x][f]+1 < ans) {
						ans = dist[y][x][f]+1;
//					cout << "goal" << endl;
					}
				}
				else if (dist[n.y][n.x][n.f] == 0 || dist[y][x][f]+1 < dist[n.y][n.x][n.f]) {
					dist[n.y][n.x][n.f] = dist[y][x][f]+1;
					Q.push(n);
				}
			}
		}
	}
	if (ans == 0) cout << -1 << endl;
	else cout << ans-1 << endl;
}

