  int y,x,S,cost;
};


state calc(int dir,state s){
  int T=0;

  int ny=s.y+dy[dir],nx=s.x+dx[dir];

  while(1){
    if(t[ny][nx]==-1)break;
    T|=(1<<t[ny][nx]);
    ny+=dy[dir];
    nx+=dx[dir];
  }

  if(T==0)s.cost=-2;
  s.y=ny;
  s.x=nx;
  s.cost++;
  s.S-=T;
  return s;
}

