            if(cur_pos == next_pos || next_field[next_pos.F += dy[i]][next_pos.S += dx[i]] == '#') continue;

            State next = mp(next_field, mp(next_turn, next_pos));
            q.push(next);
        }
    }

    cout << res << endl;

	return 0;
}

