	vs fld(h);
	REP(i, h) cin >> fld[i];
	vector<vi> id(h, vi(w, -1));
	vector<pii> place;
	int cnt = 0;
	pii init;
	REP(i, h)REP(j, w)
	{
		if (fld[i][j] == '.') continue;
		if (fld[i][j] == 'O')
		{
			init = pii(i, j);
			fld[i][j] = '.';
		}
		else
		{
			id[i][j] = cnt;
			place.push_back(pii(i, j));
			cnt++;
		}
	}
	map<pair<int, pii>, int> mp;
	queue<pair<int, pii>> que;
	que.emplace(0, init);
	while (!que.empty())
	{
		auto tmp = que.front(); que.pop();
		int bit = tmp.first, tx = tmp.second.first, ty = tmp.second.second;
		//cout << bitset<20>(bit) << " " << tx << " " << ty << endl;
		if (tx >= h - 1)
		{
