			if (!valid(nx, ny, h, w) || fld[nx][ny] == '.' || (bit >> id[nx][ny]) & 1) continue;
			vi remove;
			while (valid(nx, ny, h, w) && fld[nx][ny] != '.' && ((bit >> id[nx][ny]) & 1) == 0)
			{
				remove.push_back(id[nx][ny]);
				nx += dx[i], ny += dy[i];
			}
			if ((tx == h - 1 && ty == -1) || (tx == h - 1 && ty == w)) continue;
			int nbit = bit;
			for (auto j : remove) nbit |= 1 << j;
			pair<int, pii> nex(nbit, pii(nx, ny));
