const int inf = 1LL << 55;
const int mod = 1e9 + 7;

bool in(int x, int y) {
  return 0<=x&&x<15&&0<=y&&y<19;
}

string S[20];
int sx, sy;
map<pint, int> mp;
int dp[1<<20];

int solve(int x, int y, int bit) {
  if(y >= 19 || y == 18 && 0 <= x && x < 15) return 0;
  if(y < 0 || x < 0 || 15 <= x) return inf;
  int &res = dp[bit];
  if(~res) return res;
  res = inf;
  for(int dx = -1; dx < 2; dx++) {
    for(int dy = -1; dy < 2; dy++) {
      if(dx == 0 && dy == 0) continue;
      int nx = x + dx, ny = y + dy, nbit = bit;
      if(!in(nx, ny) || S[ny][nx] != 'X') continue;
      if((bit>>mp[pint(nx, ny)])&1) continue;
      while(in(nx, ny) && S[ny][nx] == 'X' && !(bit>>mp[pint(nx, ny)]&1)) {
	nbit |= 1<<mp[pint(nx, ny)];
	nx += dx, ny += dy;
      }
      res = min(res, solve(nx, ny, nbit) + 1);
    }
  }
  return res;
}

