        if(col < 0 || col > 14){
            return false;
        }else{
            return true;
        }
    }else{ //row >= 18
        if(row == 18 && (col < 0 || col > 14)){
            return false;
        }else{
            return true;
        }
    }
}

Info move(Info info,DIR dir){
    Info ret;

    char map[19][15];
    for(int i = 0; i < H; i++){
        for(int k = 0; k < W; k++)map[i][k] = '.';
    }

    for(int i = 0; i < info.black_num; i++){
        if(info.black_stone[i].erased == false){
            map[info.black_stone[i].row][info.black_stone[i].col] = 'X';
        }
        ret.black_stone[i].row = info.black_stone[i].row;
        ret.black_stone[i].col = info.black_stone[i].col;
        ret.black_stone[i].erased = info.black_stone[i].erased;
    }
    ret.black_num = info.black_num;
    ret.count = info.count+1;

    int next_row,next_col,index;

    switch(dir){
    case Left_Up:
        index = 0;
        break;
    case Up:
        index = 1;
        break;
    case Right_Up:
        index = 2;
        break;
    case Left:
        index = 3;
        break;
    case Right:
        index = 4;
        break;
    case Left_Down:
        index = 5;
        break;
    case Down:
        index = 6;
        break;
    case Right_Down:
        index = 7;
        break;
    }

    next_row = info.row+diff_row[index];
    next_col = info.col+diff_col[index];

    while(rangeCheck(next_row,next_col) == true && map[next_row][next_col] == 'X'){
        next_row += diff_row[index];
        next_col += diff_col[index];
    }


    if(next_row == info.row+diff_row[index] && next_col == info.col+diff_col[index]){
        ret.row = 1;
        ret.col = -1;
    }else{
        int tmp_row,tmp_col;

        tmp_row = info.row+diff_row[index];
        tmp_col = info.col+diff_col[index];

        while(tmp_row != next_row || tmp_col != next_col){
            for(int i = 0; i < ret.black_num;i++){
                if(ret.black_stone[i].erased == false && ret.black_stone[i].row == tmp_row && ret.black_stone[i].col == tmp_col){
                    ret.black_stone[i].erased = true;
                    break;
                }
            }
            tmp_row += diff_row[index];
            tmp_col += diff_col[index];
        }

        ret.row = next_row;
        ret.col = next_col;
    }

    return ret;
}

int main(){

    Info first;

    for(int i = 0; i < 20; i++)first.black_stone[i].erased = true;
    first.black_num = 0;
    char buf[15];
    int minimum = BIG_NUM,start_row,start_col;

    for(int i = 0; i < H; i++){
        scanf("%s",buf);
        for(int k = 0; k < W; k++){
            if(buf[k] == 'O'){
                start_row = i;
                start_col = k;
            }else if(buf[k] == 'X'){
                first.black_stone[first.black_num].erased = false;
                first.black_stone[first.black_num].row = i;
                first.black_stone[first.black_num++].col = k;
            }
        }
    }

    first.row = start_row;
    first.col = start_col;
    first.count = 0;

    queue<Info> Q;
    Q.push(first);

    while(!Q.empty()){

        if(Q.front().row >= 18){
            minimum = Q.front().count;
            break;
        }else{

            for(int i = 0; i < 8; i++){
                Info next = move(Q.front(),dir[i]);

                if(rangeCheck(next.row,next.col)){
                    Q.push(next);
                }
            }

            Q.pop();
        }
    }

    if(minimum == BIG_NUM){
        printf("-1\n");
    }else{
        printf("%d\n",minimum);
    }

    return 0;
}

