void dfs(int yy, int xx, int dep){
    rep(i,8){
        int nowy = yy, nowx = xx;
        for(int j = 0;;j++){
            nowy += dy[i];
            nowx += dx[i];
            if(nowy >= h){
                ans = min(ans,dep+1);
                return;
