            if(0>nowy||0>nowx||nowx>=w){
                break;
            }
            if(bo[nowy][nowx] == '.'){
                if(j == 0) break;
                if(nowy == h-1){
                    ans = min(ans,dep+1);
                    return;
                }
                int prey = nowy, prex = nowx;
                prey -= dy[i];
